import axios from 'axios';
import React, { useEffect, useState } from 'react';
import _ from 'lodash';
import { Flex, Spin } from 'antd';

const Table = () => {
  const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
  
  const API = "https://livescore-api.com/api-client/competitions/standings.json";

  const teamImages = [
    {
      team_id: "1",
      img: "data:image/png;base64,"
    },
    {
      team_id: "2",
      img: "data:image/png;base64,",
    },
    {
      team_id: "3",
      img: "data:image/png;base64,",
    },
    {
      team_id: "489",
      img: "data:image/png;base64,"
    },
    {
      team_id: "4",
      img: "data:image/png;base64,",
    },
    {
      team_id: "6",
      img: "data:image/png;base64,",
    },
    {
      team_id: "7",
      img: "https://img.uefa.com/imgml/TP/teams/logos/32x32/7889.png"
    },
    {
      team_id: "9",
      img: "data:image/png;base64,",
    },
    {
      team_id: "12",
      img: "https://img.uefa.com/imgml/TP/teams/logos/32x32/52919.png",
    },
    {
      team_id: "14",
      img: "data:image/png;base64,",
    },
    {
      team_id: "15",
      img: "data:image/png;base64,",
    },
    {
      team_id: "497",
      img: "data:image/png;base64,"
    },
    {
      team_id: "17",
      img: "data:image/png;base64,",
    },
    {
      team_id: "18",
      img: "https://img.uefa.com/imgml/TP/teams/logos/32x32/52280.png",
    },
    {
      team_id: "19",
      img: "data:image/png;base64,"
    },
    {
      team_id: "20",
      img: "data:image/png;base64,",
    },
    {
      team_id: "495",
      img:"https://img.uefa.com/imgml/TP/teams/logos/32x32/52683.png"
    },
    {
      team_id: "487",
      img:"data:image/png;base64,"
    },
    {
      team_id:"502",
      img:"data:image/png;base64,"
    },
    {
      team_id:"491",
      img:"data:image/png;base64,"
    }
    
    // ... Add other teams similarly
  ];

  const getData = async () => {
    try {
      const res = await axios.get(API, {
        params: {
          competition_id: 2,
          key: '4i8tTZxkQAxUxowe',
          secret: 'zxfFipF5MrfOaTVfc4I53gpUQWOnllCU',
        }
      });

      const apiData = res.data.data.table;

      // Merge image with team data by team_id
      const mergedData = apiData.map(team => {
        const imageObj = _.find(teamImages, { team_id: String(team.team_id) });
        return {
          ...team,
          img: imageObj?.img || 'https://via.placeholder.com/30' // fallback image
        };
      });

      setData(mergedData);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching standings:', error);
        setLoading(false);
    }
  }

  useEffect(() => {
    getData();
  }, []);

  if (loading) return <Flex align="center" gap="middle" className='flex items-center justify-center'>
  <Spin size="large" />
</Flex>
  return (
    <div className='flex items-center justify-center'>
      <div className='w-[1390px] bg-white rounded-2xl'>
        <div className='mt-6'>
          <h2 className='text-2xl ml-6'>League Phase Table</h2>
        </div>
        <table className="min-w-full table-auto text-lg mt-10">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-3 text-left">#</th>
              <th className="p-3 text-left">Club</th>
              <th className="p-3">P</th>
              <th className="p-3">W</th>
              <th className="p-3">D</th>
              <th className="p-3">L</th>
              <th className="p-3">GS</th>
              <th className="p-3">GC</th>
              <th className="p-3">GD</th>
              <th className="p-3">Pts</th>
            </tr>
          </thead>
          <tbody>
            {data.map((team, index) => (
              <tr key={index} className="border-b">
                <td className="p-3">{team.rank}</td>
                <td className="p-3 flex items-center gap-2">
                  <img
                    src={team.img}
                    alt={team.name}
                    className="w-6 h-6 rounded-full object-cover"
                  />
                  {team.name}
                </td>
                <td className="text-center">{team.matches}</td>
                <td className="text-center">{team.won}</td>
                <td className="text-center">{team.drawn}</td>
                <td className="text-center">{team.lost}</td>
                <td className="text-center">{team.goals_scored}</td>
                <td className="text-center">{team.goals_conceded}</td>
                <td className="text-center">{team.goal_diff}</td>
                <td className="text-center font-bold">{team.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Table;
